.loading {
                position: relative;
                min-height: 365px;
            }

            .loading::before {
                content: "";
                position: absolute;
                top: 0;
                left: 0;
                width: 100%;
                height: 100%;
                background-color: #fcfcfa;
            }

            .loading::after {
                content: "";
                position: absolute;
                top: 50%;
                left: 50%;
                margin-top: -30px;
                margin-left: -30px;
                width: 50px;
                height: 50px;
                border-radius: 50px;
                border: 5px solid #cfcfcf;
                border-top-color: black;
                -webkit-animation: loading 2s linear infinite;
                animation: loading 2s linear infinite;
            }

            @keyframes loading {
                0% {
                    transform: rotate(0deg);
                }
                100% {
                    transform: rotate(360deg);
                }
            }
